'use strict';

/**
 * Custom Response methods definitions
 * @type {{json: (())}}
 */
const Methods = {
    /**
     * Returns a json response with status 200
     * @returns {function()}
     */
    json(payload)
    {

    }
};

class Response
{
    /**
     * Bind Methods object functions to response object
     * @param res
     * @returns {*}
     */
    static bind(res)
    {
        Response.res = res;

        for(var method in Methods)
        {
            Response.res[method] = Methods[method];
        }

        return Response.res;
    }
}

module.exports = Response;